DROP TABLE IF EXISTS email_manager;
CREATE TABLE email_manager (
  em_id int(11) NOT NULL AUTO_INCREMENT,
  em_name varchar(255) DEFAULT NULL,
  em_language int(11) DEFAULT NULL,
  em_body text,
  em_delete tinyint(1) NOT NULL DEFAULT 0,
  em_type varchar(255) DEFAULT NULL,
  em_body_txt text,
  PRIMARY KEY (em_id)
) ENGINE=MyISAM;

INSERT INTO email_manager (em_id, em_name, em_language, em_body, em_delete, em_type, em_body_txt) VALUES
(1,	'change_order_mail',	2,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde, </strong><br />\r\n            <br />\r\n            Der Status Ihrer Bestellung {if $ORDER_NR}Nr. {$ORDER_NR}{/if} {if $ORDER_DATE}vom       {$ORDER_DATE}{/if} wurde ge&auml;ndert.<br />\r\n            <br />\r\n            {if $ORDER_LINK}Link zur Bestellung:       <a href=\"{$ORDER_LINK}\">hier klicken</a>{/if}<br />\r\n            <br />\r\n            {if $NOTIFY_COMMENTS}<br />\r\n            Anmerkungen und Kommentare zu Ihrer Bestellung:  {$NOTIFY_COMMENTS} <br />\r\n            {/if} <br />\r\n            Neuer Status:  <b>{$ORDER_STATUS}</b><br />\r\n            <br />\r\n            Bei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail. <br />\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nDer Status Ihrer Bestellung {if $ORDER_NR}Nr. {$ORDER_NR}{/if} {if $ORDER_DATE}vom {$ORDER_DATE}{/if} wurde geändert.\r\n\r\n{if $ORDER_LINK}Link zur Bestellung:\r\n{$ORDER_LINK} {/if}\r\n\r\n{if $NOTIFY_COMMENTS}Anmerkungen und Kommentare zu Ihrer Bestellung:{$NOTIFY_COMMENTS}{/if}\r\n\r\nNeuer Status: {$ORDER_STATUS}\r\n\r\nBei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail.'),
(2,	'change_order_mail',	1,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Dear customer, </strong><br />\r\n      <br />\r\n      The status of your order {if $ORDER_NR}No. {$ORDER_NR}{/if} {if $ORDER_DATE}from {$ORDER_DATE}{/if} has been changed.<br /><br /> \r\n      {if $ORDER_LINK}Link to order:\r\n      <a href=\"{$ORDER_LINK}\">click here</a>{/if}<br />\r\n      <br /> \r\n	  {if $NOTIFY_COMMENTS}<br />\r\nNote: \r\n{$NOTIFY_COMMENTS}\r\n<br />\r\n{/if}\r\n<br />\r\nNew status: \r\n<b>{$ORDER_STATUS}</b><br />\r\n<br />\r\nIf you have any questions, please reply to this e-mail. <br /></font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Dear customer,\r\n\r\nThe status of your order {if $ORDER_NR}No. {$ORDER_NR}{/if} {if $ORDER_DATE}from {$ORDER_DATE}{/if} has been changed.\r\n\r\n{if $ORDER_LINK}Link to order:\r\n{$ORDER_LINK} {/if}\r\n\r\n{if $NOTIFY_COMMENTS}Note:{$NOTIFY_COMMENTS}{/if}\r\n\r\nNew status: {$ORDER_STATUS}\r\n\r\nIf you have any questions, please reply to this e-mail.'),
(3,	'change_password_mail',	1,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Dear Customer,</strong></font> </p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Your password was successfully changed.<br />\r\n        <br />\r\n  Your new login data:</font>\r\n      </p>      \r\n      <table width=\"100%\"  border=\"0\" bgcolor=\"f1f1f1\">\r\n    <tr>\r\n      <td> <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Your e-mail-address:</strong> {$EMAIL}\r\n            <br />\r\n            <strong>Your new password:</strong> {$PASSWORD}\r\n        </font> </td>\r\n    </tr>\r\n  </table></td>\r\n  </tr>\r\n</table>\r\n\r\n 	  	 \r\n',	0,	'mail',	'Dear Customer,\r\n\r\nYour password was successfully changed.\r\n\r\nYour new login data:\r\n      \r\nYour e-mail-address: {$EMAIL}\r\nYour new password: {$PASSWORD}\r\n'),
(4,	'change_password_mail',	2,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde,</strong></font> </p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Ihr Passwort wurde erfolgreich ge&auml;ndert.<br />\r\n        <br />\r\n  Ihre neuen Logindaten:</font>\r\n      </p>      \r\n      <table width=\"100%\"  border=\"0\" bgcolor=\"f1f1f1\">\r\n    <tr>\r\n      <td> <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Ihre E-Mail-Adresse:</strong> {$EMAIL}\r\n            <br />\r\n            <strong>Ihr neues Passwort:</strong> {$PASSWORD}\r\n        </font> </td>\r\n    </tr>\r\n  </table></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nIhr Passwort wurde erfolgreich geändert.\r\n\r\nIhre neuen Logindaten:\r\n      \r\nIhre E-Mail-Adresse: {$EMAIL}\r\nIhr neues Passwort: {$PASSWORD}'),
(5,	'create_account_mail',	1,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Your account has been successfully created!</strong></font> <br />\r\n        <br />\r\n        <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">You now have access to the following features:</font><br /> \r\n        <br />\r\n        <font size=\"2\"><b><font face=\"Verdana, Arial, Helvetica, sans-serif\">-Shopping cart</font></b><font face=\"Verdana, Arial, Helvetica, sans-serif\"> - Products placed in the shopping cart will remain there until they\'ve been deleted or purchased.<br />\r\n        <b>-Address book</b> - The address book allows you to save several different shipping destinations.<br />\r\n        <b>-Order history</b> - Your order history is always available for you.<br />\r\n        <b>-Product evaluation</b> - Rate and comment our products!</font></font></p>\r\n      <p>\r\n        <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">If this account wasn\'t created by you, please contact us at \r\n        <A HREF=\"mailto:{$content.MAIL_REPLY_ADDRESS}\">{$content.MAIL_REPLY_ADDRESS}</A>\r\n        . <br />\r\n    <br />\r\n    {if $SEND_GIFT==true}\r\n      <br />\r\nAs a thank you for creating your account, you\'ve received a <b>{$GIFT_AMMOUNT}</b> gift voucher!<br />\r\n<br />\r\nRedeem your voucher with the following code <b>{$GIFT_CODE}</b> when placing an order or simply by clicking the following link <a href=\"{$GIFT_LINK}\">[redeem Voucher]</a>.{/if} {if $SEND_COUPON==true} As a thank you for creating your account, you\'ve recieved a discount voucher!<br />\r\nThe voucher details are:<br />\r\n<b>{$COUPON_DESC}</b> <br />\r\nRedeem your voucher by entering the code <b>{$COUPON_CODE}</b> during checkout process, when asked for it.{/if} \r\n<br />If you have any questions, please contact us at <A HREF=\"mailto:{$content.MAIL_REPLY_ADDRESS}\">{$content.MAIL_REPLY_ADDRESS}</A> !\r\n</font></p></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Your account has been successfully created!\r\n\r\nYou now have access to the following features:\r\n\r\n  SHOPPING CART - Products placed in the shopping cart will remain there until they\'ve been deleted or purchased.\r\n  ADDRESS BOOK - The address book allows you to save several different shipping destinations.\r\n  ORDER HISTORY - Your order history is always available for you.\r\n  PRODUCT EVALUATION - Rate and comment our products!\r\n\r\nIf this account wasn\'t created by you, please contact us at {$content.MAIL_REPLY_ADDRESS}.\r\n\r\n{if $SEND_GIFT==true}\r\nAs a thank you for creating your account, you\'ve received a {$GIFT_AMMOUNT} gift voucher! \r\nRedeem your voucher with the following code - {$GIFT_CODE} - when placing an order or simply by clicking the following link [redeem Voucher].{/if}{if $SEND_COUPON==true}\r\nAs a thank you for creating your account, you\'ve recieved a discount voucher!\r\nThe voucher details are:\r\n{$COUPON_DESC}\r\nRedeem your voucher by entering the code {$COUPON_CODE} during checkout process, when asked for it.{/if} \r\n\r\nIf you have any questions, please contact us at {$content.MAIL_REPLY_ADDRESS}!'),
(6,	'create_account_mail',	2,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde,</strong></font> <br />\r\n      <br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Sie haben soeben Ihr Kundenkonto erfolgreich erstellt, als registrierter Kunde haben sie folgende Vorteile in unserem Shop.</font><br /> \r\n      <br />\r\n      <font size=\"2\"><b><font face=\"Verdana, Arial, Helvetica, sans-serif\">-Kundenwarenkorb</font></b><font face=\"Verdana, Arial, Helvetica, sans-serif\"> - Jeder Artikel bleibt registriert bis Sie zur Kasse gehen, oder die Produkte aus dem Warenkorb entfernen.<br />\r\n      <b>-Adressbuch</b> - Wir k&ouml;nnen jetzt die Produkte zu der von Ihnen ausgesuchten Adresse senden. Der perfekte Weg ein Geburtstagsgeschenk zu versenden.<br />\r\n      <b>-Vorherige Bestellungen</b> - Sie k&ouml;nnen jederzeit Ihre vorherigen Bestellungen &uuml;berpr&uuml;fen.<br />\r\n      <b>-Meinungen &uuml;ber Produkte</b> - Teilen Sie Ihre Meinung zu unseren Produkten mit anderen Kunden.<br />      \r\n      </font></font><br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Falls Sie Fragen zu unserem Kunden-Service haben, wenden Sie sich bitte an: \r\n      {$content.MAIL_REPLY_ADDRESS}\r\n      . <br />\r\n    Achtung: Diese E-Mail-Adresse wurde uns von einem Kunden bekannt gegeben. Falls Sie sich nicht angemeldet haben, senden Sie bitte eine E-Mail an \r\n    {$content.MAIL_REPLY_ADDRESS}\r\n    . <br />\r\n    <br />\r\n	\r\n	\r\n    {if $SEND_GIFT==true}\r\n    <br />\r\n    Als kleines Willkommensgeschenk senden wir Ihnen einen Gutschein &uuml;ber:	<b>{$GIFT_AMMOUNT}</b><br />\r\n    <br />\r\nIhr pers&ouml;nlicher Gutscheincode lautet <b>{$GIFT_CODE}</b>. Sie k&ouml;nnen diese Gutschrift an der Kasse w&auml;hrend des Bestellvorganges verbuchen.<br />\r\n<br />\r\nUm den Gutschein einzul&ouml;sen klicken Sie bitte auf <a href=\"{$GIFT_LINK}\">[Gutschein Einl&ouml;sen]</a>.\r\n{/if}\r\n\r\n{if $SEND_COUPON==true}\r\n Als kleines Willkommensgeschenk senden wir Ihnen einen Kupon.<br />\r\n Kuponbeschreibung: <b>{$COUPON_DESC}</b>\r\n \r\nGeben Sie einfach Ihren pers&ouml;nlichen Code {$COUPON_CODE} w&auml;hrend des Bezahlvorganges ein\r\n\r\n{/if}\r\n\r\n</font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nSie haben soeben Ihr Kundenkonto erfolgreich erstellt, als registrierter Kunde haben sie folgende Vorteile in unserem Shop. \r\n\r\n-Kundenwarenkorb - Jeder Artikel bleibt registriert bis Sie zur Kasse gehen, oder die Produkte aus dem Warenkorb entfernen.\r\n-Adressbuch - Wir können jetzt die Produkte zu der von Ihnen ausgesuchten Adresse senden. Der perfekte Weg ein Geburtstagsgeschenk zu versenden.\r\n-Vorherige Bestellungen - Sie können jederzeit Ihre vorherigen Bestellungen überprüfen.\r\n-Meinungen über Produkte - Teilen Sie Ihre Meinung zu unseren Produkten mit anderen Kunden.      \r\n\r\nFalls Sie Fragen zu unserem Kunden-Service haben, wenden Sie sich bitte an: {$content.MAIL_REPLY_ADDRESS}\r\n\r\n\r\nAchtung: Diese E-Mail-Adresse wurde uns von einem Kunden bekannt gegeben. Falls Sie sich nicht angemeldet haben, senden Sie bitte eine E-Mail an: {$content.MAIL_REPLY_ADDRESS}\r\n    \r\n{if $SEND_GIFT==true}\r\nAls kleines Willkommensgeschenk senden wir Ihnen einen Gutschein über:	{$GIFT_AMMOUNT}\r\n\r\nIhr persönlicher Gutscheincode lautet {$GIFT_CODE}. \r\nSie können diese Gutschrift an der Kasse während des Bestellvorganges verbuchen.\r\n\r\nUm den Gutschein einzulösen verwenden Sie bitte den folgenden link {$GIFT_LINK}.\r\n{/if}\r\n\r\n{if $SEND_COUPON==true}\r\n Als kleines Willkommensgeschenk senden wir Ihnen einen Kupon.\r\n Kuponbeschreibung: {$COUPON_DESC}\r\n \r\nGeben Sie einfach Ihren persönlichen Code {$COUPON_CODE} während des Bezahlvorganges ein\r\n\r\n{/if}'),
(7,	'create_account_mail_admin',	2,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde, </strong></font><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><br />\r\n            <font size=\"2\"><br />\r\n            Es wurde ein Account f&uuml;r Sie eingerichtet, Sie k&ouml;nnen mit folgenden Daten in Unseren Shop einloggen. <br />\r\n            <br />\r\n            {if $COMMENTS} Anmerkungen: {$COMMENTS} <br />\r\n            {/if}         <br />\r\n            <br />\r\n            Ihre Logindaten f&uuml;r unseren Shop:<br />\r\n            <br />\r\n            E-Mail: {$EMAIL} <br />\r\n            Ihr Passwort: {$PASSWORD}          </font></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde, \r\n\r\nEs wurde ein Account für Sie eingerichtet, Sie können mit folgenden Daten in Unseren Shop einloggen.\r\n\r\n{if $COMMENTS} Anmerkungen: {$COMMENTS}{/if}\r\n\r\nIhre Logindaten für unseren Shop:\r\n\r\nE-Mail: {$EMAIL}\r\n\r\nIhr Passwort: {$PASSWORD}'),
(8,	'create_account_mail_admin',	1,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Dear customer, </strong></font><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><br />\r\n            <font size=\"2\"><br />\r\n            We\'ve created your customer account. <br />\r\n            <br />\r\n            {if $COMMENTS} Note: {$COMMENTS} <br />\r\n            {/if}         <br />\r\n            <br />\r\n            You can login our store with your e-mail-address and password:<br />\r\n            <br />\r\n            e-mail-address: {$EMAIL} <br />\r\n            Password: {$PASSWORD}          </font></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Dear customer,\r\n\r\nWe\'ve created your customer account.\r\n\r\n{if $COMMENTS} Note: {$COMMENTS}{/if}\r\n\r\nYou can login our store with your e-mail-address and password:\r\n\r\ne-mail-address:{$EMAIL}\r\n\r\nPassword: {$PASSWORD}'),
(9,	'gift_accepted',	2,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">&nbsp; </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n      <strong>Sehr geehrter Kunde,</strong></font> <br />\r\n      <br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Sie haben k&uuml;rzlich in unserem Online-Shop einen Gutschein bestellt, welcher aus Sicherheitsgr&uuml;nden nicht sofort freigeschaltet wurde. Dieses Guthaben steht Ihnen nun zur Verf&uuml;gung. Sie k&ouml;nnen Ihren Gutschein verbuchen und per E-Mail versenden Der von Ihnen bestellte Gutschein hat einen Wert von {$AMMOUNT}.\r\n      </font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nSie haben kürzlich in unserem Online-Shop einen Gutschein bestellt,\r\nwelcher aus Sicherheitsgründen nicht sofort freigeschaltet wurde.\r\nDieses Guthaben steht Ihnen nun zur Verfügung.\r\nSie können Ihren Gutschein verbuchen und per E-Mail versenden Der von Ihnen bestellte Gutschein hat einen Wert von {$AMMOUNT}.'),
(10,	'gift_accepted',	1,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">&nbsp; </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n      <strong>Dear customer,</strong></font> <br />\r\n      <br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">The voucher code you purchased has been activated! <br />You may now redeem your voucher and/or send it to someone by e-mail.<br />Your voucher is worth {$AMMOUNT}.\r\n      </font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Dear customer,\r\n\r\nThe voucher code you purchased has been activated!\r\nYou may now redeem your voucher and/or send it to someone by e-mail.\r\nYour voucher is worth {$AMMOUNT}.'),
(11,	'invoice_mail',	1,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde, </strong><br />\r\n            <br />\r\n            the attachment of this e-Mail includes the invoice of your order from {$ORDER_DATE}.           <br />\r\n            <br />\r\n            The state of your order you can inspect under: <a href=\"{$ORDER_LINK}\">{$ORDER_LINK}</a>.                                                                       <br />\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Dear customer,\r\n\r\nthe attachment of this e-Mail includes the invoice of your order from {$ORDER_DATE}.\r\n\r\nThe state of your order you can inspect under: {$ORDER_LINK}.'),
(12,	'invoice_mail',	2,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde, </strong><br>\r\n      <br>\r\n\r\nim Anhang dieser E-Mail übermitteln wir Ihnen die Rechnung Ihrer Bestellung vom {$ORDER_DATE}.           <br /> \r\n                                                                                                         <br /> \r\nBei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese eMail. Den Status Ihrer Bestellung können    <br /> \r\nSie einsehen unter: <a href=\"{$ORDER_LINK}\">{$ORDER_LINK}</a>.                                                                       <br /> \r\n\r\n</font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nim Anhang dieser E-Mail übermitteln wir Ihnen die Rechnung Ihrer Bestellung vom {$ORDER_DATE}.\r\n\r\nBei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese eMail, den Status Ihrer Bestellung können \r\nSie einsehen unter: {$ORDER_LINK}.'),
(13,	'newsletter_mail',	1,	'<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Thank you for subscribing!</b></p>\r\n<p>Please click the following activation link to receive newsletters. If you haven\'t subscribed to this service, please ignore this e-mail!</p>\r\n<dl><dt><b>Your activation link:</b></dt>\r\n<dd><a href=\"{$LINK}\">{$LINK}</a></dd>\r\n</dl>',	0,	'mail',	'Thank you for subscribing!\r\n\r\nPlease click the following activation link to receive newsletters. If you haven\'t subscribed to this service, please ignore this e-mail!\r\n\r\nYour activation link:\r\n{$LINK}'),
(14,	'newsletter_mail',	2,	'<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Vielen Dank f&uuml;r die Anmeldung zu unserem Newsletter.</b></font></p>\r\n<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n<p>Sie erhalten diese E-Mail, weil Sie unseren Newsletter empfangen m&ouml;chten. Bitte klicken Sie auf den Aktivierungslink damit Ihre E-Mail-Adresse f&uuml;r den Newsletterempfang freigeschaltet wird. Sollten Sie sich nicht f&uuml;r unseren Newsletter eingetragen haben bzw. den Empfang des Newsletters nicht w&uuml;nschen bitten wir Sie, den Aktivierungslink einfach zu ignorieren.</p>\r\n<dl>\r\n    <dt><b>Ihr Aktivierungslink:</b></dt>\r\n    <dd><a href=\"{$LINK}\">{$LINK}</a></dd>\r\n</dl>\r\n</font></p>',	0,	'mail',	'Vielen Dank für die Anmeldung zu unserem Newsletter.\r\n\r\nSie erhalten diese E-Mail, weil Sie unseren Newsletter empfangen möchten.\r\nBitte klicken Sie auf den Aktivierungslink, damit Ihre E-Mail-Adresse für den Newsletterempfang freigeschaltet wird.\r\n\r\nSollten Sie sich nicht für unseren Newsletter eingetragen haben bzw. den Empfang des Newsletters nicht wünschen\r\nbitten wir Sie, den Aktivierungslink einfach zu ignorieren. \r\n      \r\nIhr Aktivierungslink:\r\n{$LINK}'),
(17,	'order_mail',	1,	'{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n<table width=\"100%\" border=\"0\">\r\n  <tr> \r\n    <td>\r\n      <table width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n        <tr> \r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_customer}<br />\r\n            <br />\r\n            {if $PAYMENT_METHOD}<strong>Paymentmethod:</strong> {$PAYMENT_METHOD}<br />{/if}\r\n            <strong>Order No.:</strong> {$oID}<br />\r\n            <strong>Orderdate:</strong> {$DATE}<br />\r\n            {if $csID}<strong>Customer ID:</strong> {$csID}<br />{/if}\r\n            <strong>Your e-mail-address:</strong> {$EMAIL}<br />\r\n            </font>\r\n          </td>\r\n          <td width=\"1\"><img src=\"{$logo_path}logo.gif\"></td>\r\n        </tr>\r\n      </table>\r\n      <br /> \r\n      <table style=\"border-top:1px solid; border-bottom:1px solid;\" width=\"100%\" border=\"0\">\r\n        <tr bgcolor=\"#f1f1f1\"> \r\n          <td width=\"50%\"> <p><font size=\"1\"><strong><font face=\"Verdana, Arial, Helvetica, sans-serif\">Shippingaddress<br />\r\n          </font></strong></font></p></td>{if $address_label_payment}\r\n          <td> <p><font size=\"1\"><strong><font face=\"Verdana, Arial, Helvetica, sans-serif\">Paymentaddress<br />\r\n          </font> </strong></font></p></td>{/if}\r\n        </tr>\r\n        <tr> \r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_shipping}</font></td>\r\n          {if $address_label_payment}<td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_payment}</font></td>{/if}\r\n        </tr>\r\n      </table>\r\n      <p><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> Dear {$NAME},<br />\r\n        <br />\r\n thank you for your order. <br />\r\n {$PAYMENT_INFO_HTML}</font>\r\n        <br />{if $COMMENTS}<br />\r\n        <s