INSERT INTO content_manager VALUES ('', 0, 0, '', 3, 'Template Box 1', 'Expédition', '<p>Vous trouverez ici pour example des informations sur l\'expédition.</p><p>Vous pouvez éditer cette boîte dans la zone d\'administration à l\'adresse suivante Content -> Content Manager - Template Box 1.</p>', 0, 2, '', 1, 12, 1, '', '', '', NOW(), 1);
