INSERT INTO email_manager (em_id, em_name, em_language, em_body, em_delete, em_type, em_body_txt, em_subject) VALUES (37, 'change_order_mail', 3, '<table cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\" width=\"100%\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img alt=\"\" src=\"{$logo_path}logo.gif\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font face=\"Verdana, Arial, Helvetica, sans-serif\" size=\"2\"><strong>Sehr geehrter Kunde, </strong><br />\r\n            <br />\r\n            Der Status Ihrer Bestellung {if $ORDER_NR}Nr. {$ORDER_NR}{/if} {if $ORDER_DATE}vom       {$ORDER_DATE}{/if} wurde ge&auml;ndert.<br />\r\n            <br />\r\n            {if $ORDER_LINK}Link zur Bestellung:       <a href=\"{$ORDER_LINK}\">hier klicken</a>{/if}<br />\r\n            <br />\r\n            {if $NOTIFY_COMMENTS}<br />\r\n            Anmerkungen und Kommentare zu Ihrer Bestellung:  {$NOTIFY_COMMENTS} <br />\r\n            {/if} <br />\r\n            Neuer Status:  <b>{$ORDER_STATUS}</b><br />\r\n            <br />\r\n        {if $PARCEL_COUNT}\r\n          Die Sendung besteht aus {$PARCEL_COUNT} Paket(en).<br />\r\n        {/if}\r\n        {if $PARCEL_LINK_HTML}\r\n          <br />Sie k&ouml;nnen sich &uuml;ber den Zustellstatus durch einen Klick auf die nachstende(n) Paketnummer(n) informieren:<br />\r\n          {$PARCEL_LINK_HTML}<br />\r\n        {/if}\r\n            Bei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail. <br />\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>', 0, 'mail', 'Sehr geehrter Kunde,\r\n\r\nDer Status Ihrer Bestellung {if $ORDER_NR}Nr. {$ORDER_NR}{/if} {if $ORDER_DATE}vom {$ORDER_DATE}{/if} wurde geändert.\r\n\r\n{if $ORDER_LINK}Link zur Bestellung:\r\n{$ORDER_LINK} {/if}\r\n\r\n{if $NOTIFY_COMMENTS}Anmerkungen und Kommentare zu Ihrer Bestellung:{$NOTIFY_COMMENTS}{/if}\r\n\r\nNeuer Status: {$ORDER_STATUS}\r\n\r\n{if $PARCEL_COUNT}\r\nDie Sendung besteht aus {$PARCEL_COUNT} Paket(en).{/if}\r\n\r\n{if $PARCEL_LINK_TXT}\r\nSie können sich über den Zustellstatus durch einen Klick auf die nachstehende(n) Paketnummer(n) informieren:\r\n{$PARCEL_LINK_TXT}{/if}\r\n\r\nBei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail.', 'Ihre Bestellung {$nr} vom {$date}'),
(38,	'create_account_mail',	3,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde,</strong></font> <br />\r\n      <br />\r\n      <font size="2" face="Verdana, Arial, Helvetica, sans-serif">Sie haben soeben Ihr Kundenkonto erfolgreich erstellt,      </font><br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Falls Sie Fragen zu unserem Kundenservice haben, wenden Sie sich bitte an: \r\n      {$content.MAIL_REPLY_ADDRESS}\r\n      . <br />\r\n    Achtung: Diese E-Mail-Adresse wurde uns von einem Kunden bekannt gegeben. Falls Sie sich nicht angemeldet haben, senden Sie bitte eine E-Mail an \r\n    {$content.MAIL_REPLY_ADDRESS}\r\n    . <br />\r\n    <br />\r\n	\r\n	\r\n    {if $SEND_GIFT==true}\r\n    <br />\r\n    Als kleines Willkommensgeschenk senden wir Ihnen einen Gutschein &uuml;ber:	<b>{$GIFT_AMMOUNT}</b><br />\r\n    <br />\r\nIhr pers&ouml;nlicher Gutscheincode lautet <b>{$GIFT_CODE}</b>. Sie k&ouml;nnen diese Gutschrift an der Kasse w&auml;hrend des Bestellvorganges verbuchen.<br />\r\n<br />\r\nUm den Gutschein einzul&ouml;sen klicken Sie bitte auf <a href=\"{$GIFT_LINK}\">[Gutschein Einl&ouml;sen]</a>.\r\n{/if}\r\n\r\n{if $SEND_COUPON==true}\r\n Als kleines Willkommensgeschenk senden wir Ihnen einen Kupon.<br />\r\n Kuponbeschreibung: <b>{$COUPON_DESC}</b>\r\n \r\nGeben Sie einfach Ihren pers&ouml;nlichen Code {$COUPON_CODE} w&auml;hrend des Bezahlvorganges ein\r\n\r\n{/if}\r\n\r\n</font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nSie haben soeben Ihr Kundenkonto erfolgreich erstellt.   \r\n\r\nFalls Sie Fragen zu unserem Kundenservice haben, wenden Sie sich bitte an: {$content.MAIL_REPLY_ADDRESS}\r\n\r\n\r\nAchtung: Diese E-Mail-Adresse wurde uns von einem Kunden bekannt gegeben. Falls Sie sich nicht angemeldet haben, senden Sie bitte eine E-Mail an: {$content.MAIL_REPLY_ADDRESS}\r\n    \r\n{if $SEND_GIFT==true}\r\nAls kleines Willkommensgeschenk senden wir Ihnen einen Gutschein über:	{$GIFT_AMMOUNT}\r\n\r\nIhr persönlicher Gutscheincode lautet {$GIFT_CODE}. \r\nSie können diese Gutschrift an der Kasse während des Bestellvorganges verbuchen.\r\n\r\nUm den Gutschein einzulösen verwenden Sie bitte den folgenden link {$GIFT_LINK}.\r\n{/if}\r\n\r\n{if $SEND_COUPON==true}\r\n Als kleines Willkommensgeschenk senden wir Ihnen einen Kupon.\r\n Kuponbeschreibung: {$COUPON_DESC}\r\n \r\nGeben Sie einfach Ihren persönlichen Code {$COUPON_CODE} während des Bezahlvorganges ein\r\n\r\n{/if}', 'Sie haben soeben Ihr Kundenkonto erfolgreich erstellt'),
(39,	'create_account_mail_admin',	3,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde, </strong></font><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><br />\r\n            <font size=\"2\"><br />\r\n            Es wurde ein Account f&uuml;r Sie eingerichtet, Sie k&ouml;nnen sich mit folgenden Daten in Unseren Shop einloggen. <br />\r\n            <br />\r\n            {if $COMMENTS} Anmerkungen: {$COMMENTS} <br />\r\n            {/if}         <br />\r\n            <br />\r\n            Ihre Logindaten f&uuml;r unseren Shop:<br />\r\n            <br />\r\n            E-Mail: {$EMAIL} <br />\r\n            Ihr Passwort: {$PASSWORD}          </font></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde, \r\n\r\nEs wurde ein Account für Sie eingerichtet, Sie können sich mit folgenden Daten in Unseren Shop einloggen.\r\n\r\n{if $COMMENTS} Anmerkungen: {$COMMENTS}{/if}\r\n\r\nIhre Logindaten für unseren Shop:\r\n\r\nE-Mail: {$EMAIL}\r\n\r\nIhr Passwort: {$PASSWORD}', 'Es wurde ein Account für Sie eingerichtet'),
(40,	'gift_accepted',	3,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">&nbsp; </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n      <strong>Sehr geehrter Kunde,</strong></font> <br />\r\n      <br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Sie haben k&uuml;rzlich in unserem Online-Shop einen Gutschein bestellt, welcher aus Sicherheitsgr&uuml;nden nicht sofort freigeschaltet wurde. Dieses Guthaben steht Ihnen nun zur Verf&uuml;gung. Sie k&ouml;nnen Ihren Gutschein verbuchen und per E-Mail versenden. Der von Ihnen bestellte Gutschein hat einen Wert von {$AMMOUNT}.\r\n      </font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nSie haben kürzlich in unserem Online-Shop einen Gutschein bestellt,\r\nwelcher aus Sicherheitsgründen nicht sofort freigeschaltet wurde.\r\nDieses Guthaben steht Ihnen nun zur Verfügung.\r\nSie können Ihren Gutschein verbuchen und per E-Mail versenden Der von Ihnen bestellte Gutschein hat einen Wert von {$AMMOUNT}.', 'Ihr Guthaben steht Ihnen nun zur Verfügung'),
(41,	'invoice_mail',	3,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Sehr geehrter Kunde, </strong><br>\r\n      <br>\r\n\r\nim Anhang dieser E-Mail übermitteln wir Ihnen die Rechnung Ihrer Bestellung vom {$ORDER_DATE}.           <br /> \r\n                                                                                                         <br /> \r\nBei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail. Den Status Ihrer Bestellung können    <br /> \r\nSie einsehen unter: <a href=\"{$ORDER_LINK}\">{$ORDER_LINK}</a>.                                                                       <br /> \r\n\r\n</font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'Sehr geehrter Kunde,\r\n\r\nim Anhang dieser E-Mail übermitteln wir Ihnen die Rechnung Ihrer Bestellung vom {$ORDER_DATE}.\r\n\r\nBei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail, den Status Ihrer Bestellung können \r\nSie einsehen unter: {$ORDER_LINK}.', 'Rechnung zu Ihrer Bestellung vom {$ORDER_DATE}'),
(42,	'newsletter_mail',	3,	'<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Vielen Dank f&uuml;r die Anmeldung zu unserem Newsletter.</b></font></p>\r\n<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n<p>Sie erhalten diese E-Mail, weil Sie unseren Newsletter empfangen m&ouml;chten. Bitte klicken Sie auf den Aktivierungslink damit Ihre E-Mail-Adresse f&uuml;r den Newsletterempfang freigeschaltet wird. Sollten Sie sich nicht f&uuml;r unseren Newsletter eingetragen haben bzw. den Empfang des Newsletters nicht w&uuml;nschen bitten wir Sie, den Aktivierungslink einfach zu ignorieren.</p>\r\n<dl>\r\n    <dt><b>Ihr Aktivierungslink:</b></dt>\r\n    <dd><a href=\"{$LINK}\">{$LINK}</a></dd>\r\n</dl>\r\n</font></p>',	0,	'mail',	'Vielen Dank für die Anmeldung zu unserem Newsletter.\r\n\r\nSie erhalten diese E-Mail, weil Sie unseren Newsletter empfangen möchten.\r\nBitte klicken Sie auf den Aktivierungslink, damit Ihre E-Mail-Adresse für den Newsletterempfang freigeschaltet wird.\r\n\r\nSollten Sie sich nicht für unseren Newsletter eingetragen haben bzw. den Empfang des Newsletters nicht wünschen\r\nbitten wir Sie, den Aktivierungslink einfach zu ignorieren. \r\n      \r\nIhr Aktivierungslink:\r\n{$LINK}', 'Bitte bestätigen Sie Ihre Newsletteranmeldung'),
(43,	'new_password_mail',	3,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Passwort          ge&auml;ndert!</b></font></p>\r\n            <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n            <p>Sie erhalten diese E-Mail, weil Sie ein neues Passwort eingerichtet          bekommen wollten.<br />\r\n            Bitte loggen Sie sich mit folgendem Passwort und Ihrer E-Mail-Adresse          bei uns ein <br />\r\n            In Ihren Kontoeinstellungen k&ouml;nnen Sie ein neues Passwort vergeben.</p>\r\n            <table width=\"100%\" border=\"0\" bgcolor=\"f1f1f1\">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><b>Ihr neues Passwort:<br />\r\n                        </b>{$NEW_PASSWORD}</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p>Wir w&uuml;nschen Ihnen weiterhin viel Spa&szlig; mit unserem Angebot!</p>\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Passwort geändert!\r\n\r\nSie erhalten diese E-Mail, weil Sie ein neues Passwort eingerichtet bekommen wollten.\r\nBitte loggen Sie sich mit folgendem Passwort und Ihrer E-Mail-Adresse bei uns ein. In Ihren Kontoeinstellungen können Sie ein neues Passwort vergeben.\r\n      \r\nIhr neues Passwort: {$NEW_PASSWORD}\r\n\r\nWir wünschen Ihnen weiterhin viel Spaß mit unserem Angebot!', 'Wir haben Ihr Passwort geändert'),
(44,	'order_mail',	3,	'{config_load file=\"$language/lang_$language.conf\" section=\"checkout_confirmation\"} \r\n{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n<table width=\"100%\" border=\"0\">\r\n  <tr> \r\n    <td>\r\n      <table width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n        <tr> \r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_customer}<br />\r\n            <br />\r\n            {if $PAYMENT_METHOD}<strong>Zahlungsmethode:</strong> {$PAYMENT_METHOD}<br />{/if}\r\n            <strong>Bestellung Nr:</strong> {$oID}<br />\r\n            <strong>Bestelldatum:</strong> {$DATE}<br />\r\n            {if $csID}<strong>Kundennummer:</strong> {$csID}<br />{/if}\r\n            <strong>Ihre E-Mail-Adresse:</strong> {$EMAIL}<br />\r\n            </font>\r\n          </td>\r\n          <td width=\"1\"><img src=\"{$logo_path}logo.gif\"></td>\r\n        </tr>\r\n      </table>\r\n      <br /> \r\n      <table style=\"border-top:1px solid; border-bottom:1px solid;\" width=\"100%\" border=\"0\">\r\n        <tr bgcolor=\"#f1f1f1\"> \r\n          <td width=\"50%\"> \r\n            <p><font size=\"1\"><strong><font face=\"Verdana, Arial, Helvetica, sans-serif\">Lieferadresse <br /></font></strong></font></p>\r\n          </td>\r\n          {if $address_label_payment}\r\n          <td> \r\n            <p><font size=\"1\"><strong><font face=\"Verdana, Arial, Helvetica, sans-serif\">Rechnungsadresse <br /></font></strong></font></p>\r\n          </td>\r\n          {/if}\r\n        </tr>\r\n        <tr> \r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_shipping}</font></td>\r\n          {if $address_label_payment}\r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_payment}</font></td>\r\n          {/if}\r\n        </tr>\r\n      </table>\r\n   {if $paypal_payment_method == \"paypalplus\"}<p> <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> <strong> Zahlungsdetails </strong> </font> </p> <p> <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">  Betrag: {$paypal_amount}<br />Verwendungszweck: {$paypal_reference} <br />Zahlbar bis: {$paypal_paydate} <br /> Bank: {$paypal_bank_account} <br /> Inhaber: {$paypal_holder} <br /> IBAN: {$paypal_iban} <br /> BIC: {$paypal_bic} </font> </p>{/if}\r\n  <p><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> Hallo {$NAME},<br /><br /> vielen Dank f&uuml;r Ihre Bestellung.  \r\n        <br />{$PAYMENT_INFO_HTML}<br />\r\n        {if $COMMENTS}<br />\r\n        <strong>Ihre Anmerkungen:</strong><br />\r\n        {$COMMENTS}<br />\r\n        {/if}\r\n        <br />\r\n        </font>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n<table style=\"border-bottom:1px solid;\" width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n  <tr> \r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> <strong>Ihre bestellten Produkte nochmals zur Kontrolle: </strong></font></td>\r\n  </tr>\r\n  <tr> \r\n    <td> \r\n      <table width=\"100%\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" bgcolor=\"f1f1f1\">\r\n        <tr> \r\n          <td colspan=\"2\" style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><div align=\"center\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Stk. </strong></font></div></td>\r\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == \'true\'}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Bild </strong></font></td>\r\n          {/if}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Produkt </strong></font></td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Artikel Nr. </strong></font></td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Einzelpreis</strong></font></td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><div align=\"right\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Preis</strong></font><font size=\"1\"> </font></div></td>\r\n        </tr>\r\n        {foreach name=aussen item=order_values from=$order_data} \r\n        <tr> \r\n          <td width=\"20\" style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><div align=\"center\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_QTY}</font></div></td>\r\n          <td width=\"20\" style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><div align=\"center\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">x</font></div></td>\r\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == \'true\'}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\">\r\n            {if $order_values.PRODUCTS_IMAGE neq \'\'}              \r\n                <img src=\"{$img_path}{$order_values.PRODUCTS_IMAGE}\" style=\"{$smarty.const.SHOW_IMAGES_IN_EMAIL_STYLE}\">              \r\n            {/if}\r\n          </td>\r\n          {/if}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\">\r\n            <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n            <strong>{$order_values.PRODUCTS_NAME}</strong>\r\n            {if $order_values.PRODUCTS_ORDER_DESCRIPTION neq \'\'}<br />{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\r\n            {if $order_values.PRODUCTS_ATTRIBUTES neq \'\'}<br /><em>{$order_values.PRODUCTS_ATTRIBUTES}</em>{/if}\r\n            {if $order_values.PRODUCTS_ATTRIBUTES_DOWNLOAD == 0}\r\n                {if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}<br />&nbsp;<br />Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\r\n            {else}\r\n                {if $agree_download == \'agree\'}\r\n                        {if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}<br />&nbsp;<br />Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{#text_download_agreed#}{/if}\r\n                {else}\r\n                        {if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}<br />Lieferzeit: {#text_download_disagreed#}{/if}\r\n                {/if}\r\n            {/if}\r\n            </font>\r\n          </td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_MODEL}<br />\r\n            <em>{$order_values.PRODUCTS_ATTRIBUTES_MODEL}</em></font></td>\r\n		<td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_SINGLE_PRICE}<br />&nbsp;</font></td>\r\n		<td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><div align=\"right\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_PRICE}<br />&nbsp;</font></div></td>\r\n        </tr>\r\n        {/foreach} \r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n{foreach name=aussen item=order_total_values from=$order_total}\r\n<div align=\"right\"><font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{$order_total_values.TITLE}{$order_total_values.TEXT}</font></div>\r\n{/foreach}\r\n\r\n{if $DELIVERY_DUTY_INFO neq \'\'}\r\n<br />\r\n<table style=\"border:1px solid #a3a3a3;\" width=\"100%\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{#text_duty_info#}</font></td>\r\n  </tr>\r\n</table>\r\n<br />\r\n{/if}\r\n\r\n[SIGNATUR]\r\n\r\n{if $REVOCATION_HTML neq \'\'}\r\n<br />\r\n<font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{$REVOCATION_HTML}</font>\r\n<br />\r\n{/if}{if $PRIVACY_INFO_TXT neq \'\'}\r\n<br />\r\n<font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{$PRIVACY_INFO_TXT}</font>\r\n<br />\r\n{/if}',	0,	'mail',	'{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n{$address_label_customer}\r\n\r\n{if $PAYMENT_METHOD}Zahlungsmethode: {$PAYMENT_METHOD}{/if}\r\nBestellnummer: {$oID}\r\nDatum: {$DATE}\r\n{if $csID}Kundennummer: {$csID}{/if}\r\nIhre E-Mail-Adresse: {$EMAIL}\r\n----------------------------------------------------------------------\r\n\r\n\r\nHallo {$NAME},\r\n\r\nvielen Dank für Ihre Bestellung.\r\n\r\n{$PAYMENT_INFO_TXT}\r\n\r\n{if $COMMENTS}\r\nIhre Anmerkungen:\r\n{$COMMENTS}\r\n{/if}\r\n\r\nIhre bestellten Produkte zur Kontrolle\r\n----------------------------------------------------------------------\r\n{foreach name=aussen item=order_values from=$order_data} \r\n{$order_values.PRODUCTS_QTY} x {$order_values.PRODUCTS_NAME} {$order_values.PRODUCTS_PRICE}\r\n{if $order_values.PRODUCTS_ORDER_DESCRIPTION neq \'\'}{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\r\n{if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\r\n{if $order_values.PRODUCTS_ATTRIBUTES !=\'\'}{$order_values.PRODUCTS_ATTRIBUTES}{/if}\r\n\r\n{/foreach}\r\n\r\n{foreach name=aussen item=order_total_values from=$order_total}\r\n{$order_total_values.TITLE}{$order_total_values.TEXT}\r\n{/foreach}\r\n\r\n\r\n{if $address_label_payment}\r\nRechnungsadresse\r\n----------------------------------------------------------------------\r\n{$address_label_payment}\r\n{/if}\r\nVersandadresse \r\n----------------------------------------------------------------------\r\n{$address_label_shipping}\r\n{if $DELIVERY_DUTY_INFO neq \'\'}\r\n\r\n----------------------------------------------------------------------\r\n{#text_duty_info#}\r\n----------------------------------------------------------------------{/if}\r\n\r\n[SIGNATUR]\r\n\r\n{$REVOCATION_TXT}{$PRIVACY_INFO_TXT}', 'Ihre Bestellung {$nr} vom {$date}'),
(45,	'password_verification_mail',	3,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Bitte best&auml;tigen Sie Ihre Passwortanfrage!</b></font></p>\r\n            <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n            <p>Bitte best&auml;tigen Sie, dass Sie selbst ein neues Passwort          angefordert haben. <br />\r\n            Aus diesem Grund haben wir Ihnen diese E-Mail mit einem pers&ouml;nlichen          <br />\r\n            Best&auml;tigungslink geschickt. .\r\n            <table width=\"100%\" border=\"0\" bgcolor=\"f1f1f1\">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><b>Ihr Best&auml;tigungslink:<br />\r\n                        </b><a href=\"{$LINK}\">{$LINK}</a></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </p>\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'Bitte bestätigen Sie Ihre Passwortanfrage!\r\n\r\nBitte bestätigen Sie, dass Sie selber ein neues Passwort angefordert haben. \r\nAus diesem Grund haben wir Ihnen diese E-Mail mit einem persönlichen \r\nBestätigungslink geschickt. Wenn Sie den Link anklicken, gelangen Sie auf eine Seite, auf der Sie Ihr Passwort neu vergeben können. Dieser Link ist nur für kurze Zeit gültig! \r\n      \r\nIhr Bestätigungslink:\r\n{$LINK}', 'Bitte bestätigen Sie Ihre Passwortanfrage!'),
(46,	'send_coupon',	3,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">&nbsp;\r\n      </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n      {$MESSAGE}\r\n      <br />\r\n      <br />\r\nSie k&ouml;nnen den Gutschein bei Ihrer Bestellung einl&ouml;sen. Geben Sie daf&uuml;r Ihre Gutschein-Nummer in das Feld Gutscheine ein. <br />\r\n<br />\r\nIhr Gutschein-Nummer lautet: <strong>\r\n{$COUPON_ID}\r\n</strong><br />\r\n<br />\r\nHeben Sie Ihre Gutschein-Nummer gut auf, nur so k&ouml;nnen Sie von diesem Angebot profitieren, wenn Sie uns das n&auml;chste mal unter <a href=\"{$WEBSITE}\">{$WEBSITE}</a> besuchen. </font></td>\r\n  </tr>\r\n</table>',	0,	'mail',	'{$MESSAGE}\r\n\r\nSie k&ouml;nnen den Gutschein bei Ihrer Bestellung einlösen. Geben Sie dafür Ihren Gutschein-Nummer in das Feld Gutscheine ein.\r\n\r\nIhr Gutschein-Nummer lautet: {$COUPON_ID}\r\n\r\nHeben Sie Ihre Gutschein-Nummer gut auf, nur so können Sie von diesem Angebot profitieren\r\nwenn Sie uns das nächste mal unter {$WEBSITE} besuchen.', 'Sie haben einen Gutschein erhalten.'),
(47,	'send_gift',	3,	'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">&nbsp; </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">&nbsp;\r\n      </font>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n        {$MESSAGE}\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Gutscheinwert: {$AMMOUNT}\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Ihr Gutscheincode lautet: {$GIFT_ID}\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Um Ihren Gutschein zu verbuchen, klicken Sie auf den nachfolgenden Link - <a href=\"{$GIFT_LINK}\">{$GIFT_LINK}</a>.\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Falls es wider Erwarten zu Problemen beim Verbuchen kommen sollte, besuchen Sie unsere Webseite <a href=\"{$WEBSITE}\">{$WEBSITE}</a> und geben den Gutschein-Code bitte manuell ein.\r\n      </font></p></td>\r\n  </tr>\r\n</table>\r\n<p>&nbsp;</p>',	0,	'mail',	'{$MESSAGE}\r\n  \r\nGutscheinwert {$AMMOUNT}\r\n\r\nIhr Gutscheincode lautet: {$GIFT_ID}\r\n\r\nUm Ihren Gutschein zu verbuchen, klicken Sie auf den nachfolgenden Link - {$GIFT_LINK}.\r\n\r\nFalls es wider Erwarten zu Problemen beim verbuchen kommen sollte, besuchen Sie unsere Webseite {$WEBSITE} und geben den Gutschein-Code bitte manuell ein.', 'Sie haben einen Gutschein erhalten.'),
(48,	'send_gift_to_friend',	3,	'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">---------------------------------------------------------------------------------------- <br />\r\n            Herzlichen Gl&uuml;ckwunsch, Sie haben einen Gutschein &uuml;ber <b>{$AMMOUNT} </b>erhalten ! <br />\r\n            ----------------------------------------------------------------------------------------<br />\r\n            <br />\r\n            Dieser Gutschein wurde Ihnen &uuml;bermittelt von {$FROM_NAME},<br />\r\n            Mit der Nachricht:<br />\r\n            <br />\r\n            {$MESSAGE}<br />\r\n            <br />\r\n            Ihr pers&ouml;nlicher Gutscheincode lautet <strong>{$GIFT_CODE}</strong>. Sie k&ouml;nnen diese Gutschrift entweder w&auml;hrend dem Bestellvorgang verbuchen.<br />\r\n            <br />\r\n            Um den Gutschein einzul&ouml;sen klichen Sie bitte auf <a href=\"{$GIFT_LINK}\">{$GIFT_LINK}</a> <br />\r\n            <br />\r\n            Falls es mit dem obigen Link zu Problemen beim Einl&ouml;sen kommen sollte, <br />\r\n            k&ouml;nnen Sie den Betrag w&auml;hrend des Bestellvorganges verbuchen. </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>',	0,	'mail',	'----------------------------------------------------------------------------------------\r\n  Herzlichen Glückwunsch, Sie haben einen Gutschein über {$AMMOUNT} erhalten !\r\n----------------------------------------------------------------------------------------\r\n\r\nDieser Gutschein wurde Ihnen übermittelt von {$FROM_NAME},\r\nMit der Nachricht:\r\n\r\n{$MESSAGE}\r\n\r\nIhr persönlicher Gutscheincode lautet {$GIFT_CODE}. \r\n\r\nUm den Gutschein einzulösen klicken Sie bitte auf {$GIFT_LINK}\r\n\r\nFalls es mit dem obigen Link Probleme beim Einlösen kommen sollte,\r\nkönnen Sie den Betrag während des Bestellvorganges verbuchen.','Sie haben einen Gutschein von einem Freund erhalten.'),
(49,	'sepa_info',	3,	'<p>Den Rechnungsbetrag von {$PAYMENT_BANKTRANSFER_TOTAL} ziehen wir als SEPA-Lastschrift zum F&auml;lligkeitstag {$PAYMENT_BANKTRANSFER_DUE_DATE}<br />\r\nzu Ihrer Mandatsreferenz {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}<br />\r\nund unserer Gl&auml;ubiger-ID {$PAYMENT_BANKTRANSFER_CREDITOR_ID}<br />\r\nvon Ihrem Konto {$PAYMENT_BANKTRANSFER_IBAN}<br />\r\nbei der {$PAYMENT_BANKTRANSFER_BANKNAME} ein.</p>\r\n<p>Bitte stellen Sie sicher, dass gen&uuml;gend Geld f&uuml;r die Zahlung auf dem Konto verf&uuml;gbar ist.</p>',	0,	'mail',	'Den Rechnungsbetrag von{$PAYMENT_BANKTRANSFER_TOTAL} EUR ziehen wir als SEPA-Lastschrift zum Fälligkeitstag {$PAYMENT_BANKTRANSFER_DUE_DATE}\r\nzu Ihrer Mandatsreferenz {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}\r\nund unserer Gläubiger-Identifikationsnummer {$PAYMENT_BANKTRANSFER_CREDITOR_ID}\r\nvon Ihrem Konto {$PAYMENT_BANKTRANSFER_IBAN}\r\nbei der {$PAYMENT_BANKTRANSFER_BANKNAME} ein.\r\n\r\nBitte stellen Sie sicher, dass genügend Geld für die Zahlung auf dem Konto verfügbar ist.', ''),
(50,	'sepa_mail',	3,	'<p>{config_load file=&quot;$language/lang_$language.conf&quot; section=&quot;duty_info&quot;}  {$PAYMENT_INFO_HTML}<br />\r\n[SIGNATUR]</p>',	0,	'mail',	'{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n\r\n{$PAYMENT_INFO_TXT}\r\n\r\n[SIGNATUR]', 'Ihre Bestellung mit SEPA Lastschrift.'),
(51,	'signatur',	3,	'<div><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">__________________________________________________________________<br />\r\n<br />\r\nFirma<br />\r\nAdresse<br />\r\nOrt<br />\r\nHomepage<br />\r\nE-Mail<br />\r\nFon:<br />\r\nFax:<br />\r\nUSt-IdNr.:<br />\r\nHandelsregister<br />\r\nGesch&auml;ftsf&uuml;hrer:<br />\r\n</font></div>',	0,	'mail',	'__________________________________________________________________\r\n\r\nFirma\r\nAdresse\r\nOrt\r\nHomepage\r\nE-Mail\r\nFon:\r\nFax:\r\nUSt-IdNr.:\r\nHandelsregister\r\nGeschäftsführer:', ''),
(52,  'stock_reorder_mail', 3,  '<p>Nach dem letzten Verkauf des Produktes  {$PRODUCTS_NAME} ist der Bestand auf {$PRODUCTS_CURRENT_QTY}  gesunken. Bitte bestellen Sie nach.</p>' , 0, 'mail',  'Nach dem letzten Verkauf des Produktes  {$PRODUCTS_NAME} ist der Bestand auf {$PRODUCTS_CURRENT_QTY}  gesunken. Bitte bestellen Sie nach.', 'Shopnachricht: Bitte bestellen Sie nach. '),
(53,  'reminder_mail', 3, '<table  width="100%" border="0" align="center" cellpadding="4" cellspacing="0"><tr><td style="border-bottom: 1px solid; border-color: #cccccc;"><div align="right"><img src="{$logo_path}logo.gif"></div></td></tr><tr><td><font size="2" face="Verdana, Arial, Helvetica, sans-serif"><strong>Sehr geehrter Kunde, </strong><br><br>im Anhang dieser E-Mail senden wir Ihnen die Erinnerung an Ihre Bestellung von {$ORDER_DATE}.<br /><br />Bei Fragen zu Ihrer Bestellung antworten Sie bitte auf diese E-Mail. Den Status Ihrer Bestellung können<br />Sie einsehen unter: <a href="{$ORDER_LINK}">{$ORDER_LINK}</a>.<br /></font></td></tr></table>', 0, 'mail', 'Sehr geehrter Kunde,im Anhang dieser E-Mail senden wir Ihnen die Erinnerung an Ihre Bestellung von {$ORDER_DATE}.Wenn Sie Fragen zu Ihrer Bestellung haben, antworten Sie bitte auf diese E-Mail, Sie können den Status Ihrer Bestellung überprüfen.Sie sehen unter: {$ORDER_LINK}.', 'Erinnerung an Ihre Bestellung von {$ORDER_DATE}');
