INSERT INTO email_manager (em_name, em_language, em_body, em_delete, em_type, em_body_txt, em_subject) VALUES 
('change_order_mail',3,'<table cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\" width=\"100%\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img alt=\"\" src=\"{$logo_path}logo.gif\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font face=\"Verdana, Arial, Helvetica, sans-serif\" size=\"2\"><strong>Cher client, </strong><br />\r\n            <br />\r\n            Le statut de votre commande {if $ORDER_NR}No. {$ORDER_NR}{/if} {if $ORDER_DATE}vomir       {$ORDER_DATE}{/if} a été modifié.<br />\r\n            <br />\r\n            {if $ORDER_LINK}Lien pour commander:       <a href=\"{$ORDER_LINK}\">cliquez ici</a>{/if}<br />\r\n            <br />\r\n            {if $NOTIFY_COMMENTS}<br />\r\n            Remarques et commentaires sur votre commande:  {$NOTIFY_COMMENTS} <br />\r\n            {/if} <br />\r\n            Nouveau statut:  <b>{$ORDER_STATUS}</b><br />\r\n            <br />\r\n        {if $PARCEL_COUNT}\r\n          L\'envoi se compose de {$PARCEL_COUNT} Forfait(s).<br />\r\n        {/if}\r\n        {if $PARCEL_LINK_HTML}\r\n          <br />Vous pouvez connaître l\'état d\'avancement de la livraison en cliquant sur le(s) numéro(s) de colis suivant(s):<br />\r\n          {$PARCEL_LINK_HTML}<br />\r\n        {/if}\r\n            Si vous avez des questions concernant votre commande, veuillez répondre à cet e-mail. <br />\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>','0','mail','Cher client,\r\n\r\nLe statut de votre commande {if $ORDER_NR}Nr. {$ORDER_NR}{/if} {if $ORDER_DATE}vomir {$ORDER_DATE}{/if} a été modifié.\r\n\r\n{if $ORDER_LINK}Lien pour commander:\r\n{$ORDER_LINK} {/if}\r\n\r\n{if $NOTIFY_COMMENTS}Remarques et commentaires sur votre commande:{$NOTIFY_COMMENTS}{/if}\r\n\r\nNouveau statut: {$ORDER_STATUS}\r\n\r\n{if $PARCEL_COUNT}\r\nL\'envoi se compose de {$PARCEL_COUNT} Forfait(s).{/if}\r\n\r\n{if $PARCEL_LINK_TXT}\r\nVous pouvez connaître l\'état d\'avancement de la livraison en cliquant sur le(s) numéro(s) de colis ci-dessous:\r\n{$PARCEL_LINK_TXT}{/if}\r\n\r\nSi vous avez des questions concernant votre commande, veuillez répondre à cet e-mail.','Votre commande {$nr} vomir {$date}'),
('create_account_mail',3,'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Cher client,</strong></font> <br />\r\n      <br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Vous venez de créer avec succès votre compte client,      </font><br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Si vous avez des questions au sujet de notre service à la clientèle, veuillez communiquer avec nous : \r\n      {$content.MAIL_REPLY_ADDRESS}\r\n      . <br />\r\n    Attention : Cette adresse e-mail nous a été donnée par un client. Si vous n\'êtes pas encore inscrit, veuillez envoyer un courriel à \r\n    {$content.MAIL_REPLY_ADDRESS}\r\n    . <br />\r\n    <br />\r\n	\r\n	\r\n    {if $SEND_GIFT==true}\r\n    <br />\r\n    En guise de petit cadeau de bienvenue, nous vous enverrons un bon d\'achat pour:	<b>{$GIFT_AMMOUNT}</b><br />\r\n    <br />\r\nVous pouvez réserver ce crédit à la caisse pendant le processus de commande..<br />\r\n<br />\r\nUm den Gutschein einzulösen klicken Sie bitte auf <a href=\"{$GIFT_LINK}\">[Gutschein Einlösen]</a>.\r\n{/if}\r\n\r\n{if $SEND_COUPON==true}\r\n En guise de petit cadeau de bienvenue, nous vous envoyons un coupon.\r\n description du bon: <b>{$COUPON_DESC}</b>\r\n \r\nIl vous suffit d\'entrer votre code personnel {$COUPON_CODE} pendant le processus de paiement.\r\n{/if}\r\n\r\n</font></td>\r\n  </tr>\r\n</table>','0','mail','Cher client,\r\n\r\nVous venez de créer votre compte avec succès.   \r\n\r\nSi vous avez des questions au sujet de notre service à la clientèle, veuillez communiquer avec: {$content.MAIL_REPLY_ADDRESS}\r\n\r\n\r\nAttention : Cette adresse e-mail nous a été donnée par un client. Si vous n\'êtes pas encore inscrit, veuillez envoyer un courriel à: {$content.MAIL_REPLY_ADDRESS}\r\n    \r\n{if $SEND_GIFT==true}\r\nEn guise de petit cadeau de bienvenue, nous vous enverrons un bon d\'achat pour:	{$GIFT_AMMOUNT}\r\n\r\nVotre code de bon personnel est {$GIFT_CODE}. \r\nVous pouvez réserver ce crédit à la caisse pendant le processus de commande..\r\n\r\nPour utiliser le bon d\'achat, veuillez utiliser le lien suivant {$GIFT_LINK}.\r\n{/if}\r\n\r\n{if $SEND_COUPON==true}\r\n Comme petit cadeau de bienvenue, nous vous enverrons un coupon de réduction.\r\n description du bon: {$COUPON_DESC}\r\n \r\nIl vous suffit d\'entrer votre code personnel {$COUPON_CODE} pendant le processus de paiement.\r\n\r\n{/if}','Vous venez de créer avec succès votre compte client'),
('create_account_mail_admin',3,'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Cher client, </strong></font><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><br />\r\n            <font size=\"2\"><br />\r\n            Un compte a été créé pour vous, vous pouvez vous connecter à notre boutique avec les données suivantes. <br />\r\n            <br />\r\n            {if $COMMENTS} Annotations: {$COMMENTS} <br />\r\n            {/if}         <br />\r\n            <br />\r\n            Vos données d\'accès à notre boutique:<br />\r\n            <br />\r\n            E-Mail: {$EMAIL} <br />\r\n            Votre mot de passe: {$PASSWORD}          </font></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>','0','mail','Cher client, \r\n\r\nUn compte a été créé pour vous, vous pouvez vous connecter à notre boutique avec les données suivantes.\r\n\r\n{if $COMMENTS} annotations: {$COMMENTS}{/if}\r\n\r\nVos données d\'accès à notre boutique:\r\n\r\nE-Mail: {$EMAIL}\r\n\r\nVotre mot de passe: {$PASSWORD}','Un compte a été créé pour vous'),
('gift_accepted',3,'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">  </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n      <strong>Cher client,</strong></font> <br />\r\n      <br />\r\n      <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Vous avez récemment commandé un bon d\'achat dans notre boutique en ligne, qui n\'a pas été activé immédiatement pour des raisons de sécurité. Ce crédit est maintenant disponible pour vous. Vous pouvez réserver votre bon et l\'envoyer par e-mail. Le bon que vous avez commandé a une valeur de {$AMMOUNT}.\r\n      </font></td>\r\n  </tr>\r\n</table>','0','mail','Cher client,\r\n\r\nVous avez récemment commandé un bon d\'achat dans notre boutique en ligne,\r\nqui, pour des raisons de sécurité, n\'a pas été immédiatement déverrouillé.\r\nCe crédit est maintenant disponible pour vous.\r\nVous pouvez réserver votre bon d\'achat et l\'envoyer par e-mail Le bon d\'achat que vous avez commandé a une valeur de {$AMMOUNT}.','Votre crédit est maintenant à votre disposition'),
('invoice_mail',3,'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr> \r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr> \r\n    <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Cher client, </strong><br>\r\n      <br>\r\n\r\ndans la pièce jointe de cet e-mail, nous vous enverrons la facture de votre commande à partir du {$ORDER_DATE}.           <br /> \r\n                                                                                                         <br /> \r\nSi vous avez des questions concernant votre commande, veuillez répondre à cet e-mail. Vous pouvez vérifier l\'état de votre commande    <br /> \r\nVous pouvez voir sous: <a href=\"{$ORDER_LINK}\">{$ORDER_LINK}</a>.                                                                       <br /> \r\n\r\n</font></td>\r\n  </tr>\r\n</table>','0','mail','Cher client,\r\n\r\ndans la pièce jointe de cet e-mail, nous vous enverrons la facture de votre commande à partir du {$ORDER_DATE}.\r\n\r\nSi vous avez des questions concernant votre commande, veuillez répondre à cet e-mail, le statut de votre commande peut être le suivant  \r\nVous voyez ci-dessous : {$ORDER_LINK}.','Facture pour votre commande de {$ORDER_DATE}'),
('newsletter_mail',3,'<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Merci beaucoup de vous être inscrit à notre newsletter.</b></font></p>\r\n<p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n<p>Vous recevez cet e-mail parce que vous souhaitez recevoir notre newsletter. Veuillez cliquer sur le lien d\'activation pour activer votre adresse e-mail afin de recevoir la newsletter. Si vous n\'êtes pas abonné à notre newsletter ou si vous ne souhaitez pas la recevoir, veuillez ignorer le lien d\'activation..</p>\r\n<dl>\r\n    <dt><b>Votre lien d\'activation:</b></dt>\r\n    <dd><a href=\"{$LINK}\">{$LINK}</a></dd>\r\n</dl>\r\n</font></p>','0','mail','Merci beaucoup de vous être inscrit à notre newsletter.\r\n\r\nVous recevez cet e-mail parce que vous souhaitez recevoir notre newsletter.\r\nVeuillez cliquer sur le lien d\'activation pour activer votre adresse e-mail afin de recevoir la newsletter.\r\n\r\nSi vous n\'êtes pas abonné à notre newsletter ou si vous ne souhaitez pas recevoir la newsletter\r\nnous vous demandons d\'ignorer simplement le lien d\'activation. \r\n      \r\nVotre lien d\'activation :\r\n{$LINK}','Veuillez confirmer votre inscription à la newsletter'),
('new_password_mail',3,'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Mot de passe changé !</b></font></p>\r\n            <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n            <p>Vous avez reçu cet e-mail parce que vous vouliez obtenir un nouveau mot de passe.<br />\r\n            Veuillez vous connecter avec le mot de passe suivant et votre adresse e-mail <br />\r\n            Vous pouvez attribuer un nouveau mot de passe dans les paramètres de votre compte..</p>\r\n            <table width=\"100%\" border=\"0\" bgcolor=\"f1f1f1\">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><b>Votre nouveau mot de passe:<br />\r\n                        </b>{$NEW_PASSWORD}</td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            <p>Nous vous souhaitons beaucoup de plaisir avec notre offre dans le futur!</p>\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>','0','mail','Mot de passe changé !\r\n\r\nVous avez reçu cet e-mail parce que vous vouliez obtenir un nouveau mot de passe.\r\nVeuillez vous connecter avec le mot de passe suivant et votre adresse e-mail. Vous pouvez attribuer un nouveau mot de passe dans les paramètres de votre compte.\r\n      \r\nVotre nouveau mot de passe: {$NEW_PASSWORD}\r\n\r\nNous vous souhaitons beaucoup de plaisir avec notre offre dans le futur!','Nous avons changé votre mot de passe'),
('order_mail',3,'{config_load file=\"$language/lang_$language.conf\" section=\"checkout_confirmation\"} \r\n{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n<table width=\"100%\" border=\"0\">\r\n  <tr> \r\n    <td>\r\n      <table width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n        <tr> \r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_customer}<br />\r\n            <br />\r\n            {if $PAYMENT_METHOD}<strong>Moyen de paiement:</strong> {$PAYMENT_METHOD}<br />{/if}\r\n            <strong>Numéro de commande:</strong> {$oID}<br />\r\n            <strong>Date de commande:</strong> {$DATE}<br />\r\n            {if $csID}<strong>Numéro de client:</strong> {$csID}<br />{/if}\r\n            <strong>Votre adresse email:</strong> {$EMAIL}<br />\r\n            </font>\r\n          </td>\r\n          <td width=\"1\"><img src=\"{$logo_path}logo.gif\"></td>\r\n        </tr>\r\n      </table>\r\n      <br /> \r\n      <table style=\"border-top:1px solid; border-bottom:1px solid;\" width=\"100%\" border=\"0\">\r\n        <tr bgcolor=\"#f1f1f1\"> \r\n          <td width=\"50%\"> \r\n            <p><font size=\"1\"><strong><font face=\"Verdana, Arial, Helvetica, sans-serif\">adresse de livraison <br /></font></strong></font></p>\r\n          </td>\r\n          {if $address_label_payment}\r\n          <td> \r\n            <p><font size=\"1\"><strong><font face=\"Verdana, Arial, Helvetica, sans-serif\">adresse de facturation <br /></font></strong></font></p>\r\n          </td>\r\n          {/if}\r\n        </tr>\r\n        <tr> \r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_shipping}</font></td>\r\n          {if $address_label_payment}\r\n          <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$address_label_payment}</font></td>\r\n          {/if}\r\n        </tr>\r\n      </table>\r\n   {if $paypal_payment_method == \"paypalplus\"}<p> <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> <strong> informations de paiement </strong> </font> </p> <p> <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">  montant: {$paypal_amount}<br />but de l\'utilisation: {$paypal_reference} <br />Payable jusqu\'au: {$paypal_paydate} <br /> paillasse: {$paypal_bank_account} <br /> titulaire: {$paypal_holder} <br /> IBAN: {$paypal_iban} <br /> BIC: {$paypal_bic} </font> </p>{/if}\r\n  <p><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> Bonjour {$NAME},<br /><br /> merci beaucoup pour votre commande.  \r\n        <br />{$PAYMENT_INFO_HTML}<br />\r\n        {if $COMMENTS}<br />\r\n        <strong>vos commentaires:</strong><br />\r\n        {$COMMENTS}<br />\r\n        {/if}\r\n        <br />\r\n        </font>\r\n      </p>\r\n    </td>\r\n  </tr>\r\n</table>\r\n<table style=\"border-bottom:1px solid;\" width=\"100%\" border=\"0\" cellpadding=\"0\" cellspacing=\"0\">\r\n  <tr> \r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> <strong>Vos produits commandés à nouveau pour le contrôle: </strong></font></td>\r\n  </tr>\r\n  <tr> \r\n    <td> \r\n      <table width=\"100%\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" bgcolor=\"f1f1f1\">\r\n        <tr> \r\n          <td colspan=\"2\" style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><div align=\"center\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Stk. </strong></font></div></td>\r\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == \'true\'}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Bild </strong></font></td>\r\n          {/if}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Produkt </strong></font></td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Artikel Nr. </strong></font></td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Einzelpreis</strong></font></td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><div align=\"right\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong> \r\n            Preis</strong></font><font size=\"1\"> </font></div></td>\r\n        </tr>\r\n        {foreach name=aussen item=order_values from=$order_data} \r\n        <tr> \r\n          <td width=\"20\" style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><div align=\"center\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_QTY}</font></div></td>\r\n          <td width=\"20\" style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><div align=\"center\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">x</font></div></td>\r\n          {if $smarty.const.SHOW_IMAGES_IN_EMAIL == \'true\'}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\">\r\n            {if $order_values.PRODUCTS_IMAGE neq \'\'}              \r\n                <img src=\"{$img_path}{$order_values.PRODUCTS_IMAGE}\" style=\"{$smarty.const.SHOW_IMAGES_IN_EMAIL_STYLE}\">              \r\n            {/if}\r\n          </td>\r\n          {/if}\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\">\r\n            <font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n            <strong>{$order_values.PRODUCTS_NAME}</strong>\r\n            {if $order_values.PRODUCTS_ORDER_DESCRIPTION neq \'\'}<br />{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\r\n            {if $order_values.PRODUCTS_ATTRIBUTES neq \'\'}<br /><em>{$order_values.PRODUCTS_ATTRIBUTES}</em>{/if}\r\n            {if $order_values.PRODUCTS_ATTRIBUTES_DOWNLOAD == 0}\r\n                {if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}<br /> <br />Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\r\n            {else}\r\n                {if $agree_download == \'agree\'}\r\n                        {if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}<br /> <br />Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{#text_download_agreed#}{/if}\r\n                {else}\r\n                        {if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}<br />Lieferzeit: {#text_download_disagreed#}{/if}\r\n                {/if}\r\n            {/if}\r\n            </font>\r\n          </td>\r\n          <td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_MODEL}<br />\r\n            <em>{$order_values.PRODUCTS_ATTRIBUTES_MODEL}</em></font></td>\r\n		<td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_SINGLE_PRICE}<br /> </font></td>\r\n		<td style=\"border-right: 2px solid; border-bottom: 2px solid; border-color: #ffffff;\" width=\"150\"><div align=\"right\"><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">{$order_values.PRODUCTS_PRICE}<br /> </font></div></td>\r\n        </tr>\r\n        {/foreach} \r\n      </table>\r\n    </td>\r\n  </tr>\r\n</table>\r\n{foreach name=aussen item=order_total_values from=$order_total}\r\n<div align=\"right\"><font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{$order_total_values.TITLE}{$order_total_values.TEXT}</font></div>\r\n{/foreach}\r\n\r\n{if $DELIVERY_DUTY_INFO neq \'\'}\r\n<br />\r\n<table style=\"border:1px solid #a3a3a3;\" width=\"100%\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{#text_duty_info#}</font></td>\r\n  </tr>\r\n</table>\r\n<br />\r\n{/if}\r\n\r\n[SIGNATUR]\r\n\r\n{if $REVOCATION_HTML neq \'\'}\r\n<br />\r\n<font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{$REVOCATION_HTML}</font>\r\n<br />\r\n{/if}{if $PRIVACY_INFO_TXT neq \'\'}\r\n<br />\r\n<font size=\"1\" face=\"Arial, Helvetica, sans-serif\">{$PRIVACY_INFO_TXT}</font>\r\n<br />\r\n{/if}','0','mail','{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n{$address_label_customer}\r\n\r\n{if $PAYMENT_METHOD}moyen de paiement: {$PAYMENT_METHOD}{/if}\r\nnuméro de commande: {$oID}\r\nsortir avec: {$DATE}\r\n{if $csID}numéro de client: {$csID}{/if}\r\nVotre adresse email: {$EMAIL}\r\n----------------------------------------------------------------------\r\n\r\n\r\nBonjour {$NAME},\r\n\r\nmerci beaucoup pour votre commande.\r\n\r\n{$PAYMENT_INFO_TXT}\r\n\r\n{if $COMMENTS}\r\nvos commentaires:\r\n{$COMMENTS}\r\n{/if}\r\n\r\nVos produits commandés pour le contrôle\r\n----------------------------------------------------------------------\r\n{foreach name=aussen item=order_values from=$order_data} \r\n{$order_values.PRODUCTS_QTY} x {$order_values.PRODUCTS_NAME} {$order_values.PRODUCTS_PRICE}\r\n{if $order_values.PRODUCTS_ORDER_DESCRIPTION neq \'\'}{$order_values.PRODUCTS_ORDER_DESCRIPTION}{/if}\r\n{if $order_values.PRODUCTS_SHIPPING_TIME neq \'\'}Lieferzeit: {$order_values.PRODUCTS_SHIPPING_TIME}{/if}\r\n{if $order_values.PRODUCTS_ATTRIBUTES !=\'\'}{$order_values.PRODUCTS_ATTRIBUTES}{/if}\r\n\r\n{/foreach}\r\n\r\n{foreach name=aussen item=order_total_values from=$order_total}\r\n{$order_total_values.TITLE}{$order_total_values.TEXT}\r\n{/foreach}\r\n\r\n\r\n{if $address_label_payment}\r\nadresse de facturation\r\n----------------------------------------------------------------------\r\n{$address_label_payment}\r\n{/if}\r\nadresse de livraison \r\n----------------------------------------------------------------------\r\n{$address_label_shipping}\r\n{if $DELIVERY_DUTY_INFO neq \'\'}\r\n\r\n----------------------------------------------------------------------\r\n{#text_duty_info#}\r\n----------------------------------------------------------------------{/if}\r\n\r\n[SIGNATUR]\r\n\r\n{$REVOCATION_TXT}{$PRIVACY_INFO_TXT}','Votre commande {$nr} de la part de {$date}'),
('password_verification_mail',3,'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td>\r\n            <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><b>Veuillez confirmer votre demande de mot de passe!</b></font></p>\r\n            <font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n            <p>Veuillez confirmer vous-même que vous avez demandé un nouveau mot de passe.. <br />\r\n            C\'est pour cette raison que nous vous avons envoyé ce courriel avec une adresse personnelle.          <br />\r\n            Lien de confirmation envoyé. .\r\n            <table width=\"100%\" border=\"0\" bgcolor=\"f1f1f1\">\r\n                <tbody>\r\n                    <tr>\r\n                        <td><b>Votre lien de confirmation:<br />\r\n                        </b><a href=\"{$LINK}\">{$LINK}</a></td>\r\n                    </tr>\r\n                </tbody>\r\n            </table>\r\n            </p>\r\n            </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>','0','mail','Veuillez confirmer votre demande de mot de passe !\r\n\r\nVeuillez confirmer vous-même que vous avez demandé un nouveau mot de passe. \r\nC\'est pour cette raison que nous vous avons envoyé ce courriel avec une adresse personnelle. \r\nLien de confirmation envoyé. Si vous cliquez sur le lien, vous serez dirigé vers une page où vous pourrez réinitialiser votre mot de passe. Ce lien n\'est valable que pour une courte durée ! \r\n      \r\nVotre lien de confirmation :\r\n{$LINK}','Veuillez confirmer votre demande de mot de passe !'),
('send_coupon',3,'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\"> \r\n      </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n      {$MESSAGE}\r\n      <br />\r\n      <br />\r\nVous pouvez utiliser le bon d\'achat avec votre commande. Entrez votre numéro de bon d\'achat dans le champ Bon d\'achat. <br />\r\n<br />\r\nVotre numéro de bon est: <strong>\r\n{$COUPON_ID}\r\n</strong><br />\r\n<br />\r\nConservez votre numéro de bon d\'achat en lieu sûr, c\'est le seul moyen de bénéficier de cette offre.\r\nla prochaine fois que tu nous prends sous ton aile <a href=\"{$WEBSITE}\">{$WEBSITE}</a> visiter. </font></td>\r\n  </tr>\r\n</table>','0','mail','{$MESSAGE}\r\n\r\nVous pouvez utiliser le bon d\'achat avec votre commande. Entrez votre numéro de bon d\'achat dans le champ Bon d\'achat.\r\n\r\nVotre numéro de bon est: {$COUPON_ID}\r\n\r\nConservez votre numéro de bon d\'achat en lieu sûr, c\'est le seul moyen de bénéficier de cette offre.\r\nla prochaine fois que tu nous prends sous ton aile. {$WEBSITE} visiter.','Vous avez reçu un bon d\'achat.'),
('send_gift',3,'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\">\r\n  <tr>\r\n    <td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td>\r\n  </tr>\r\n  <tr>\r\n    <td><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">  </font><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"> \r\n      </font>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">\r\n        {$MESSAGE}\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Valeur du bon: {$AMMOUNT}\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Votre code promo est: {$GIFT_ID}\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Pour réserver votre bon, cliquez sur le lien suivant - <a href=\"{$GIFT_LINK}\">{$GIFT_LINK}</a>.\r\n      </font></p>\r\n      <p><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">Si, contre toute attente, vous rencontrez des problèmes lors de la réservation, veuillez visiter notre site web. <a href=\"{$WEBSITE}\">{$WEBSITE}</a> et veuillez entrer le code du bon manuellement.\r\n      </font></p></td>\r\n  </tr>\r\n</table>\r\n<p> </p>','0','mail','{$MESSAGE}\r\n  \r\nvaleur du bon {$AMMOUNT}\r\n\r\nVotre code promo est: {$GIFT_ID}\r\n\r\nPour réserver votre bon, cliquez sur le lien suivant - {$GIFT_LINK}.\r\n\r\nSi, contre toute attente, vous rencontrez des problèmes lors de la réservation, veuillez visiter notre site web. {$WEBSITE} et veuillez entrer le code du bon manuellement.','Vous avez reçu un bon d\'achat.'),
('send_gift_to_friend',3,'<table width=\"100%\" cellspacing=\"0\" cellpadding=\"4\" border=\"0\" align=\"center\">\r\n    <tbody>\r\n        <tr>\r\n            <td style=\"border-bottom: 1px solid; border-color: #cccccc;\">\r\n            <div align=\"right\"><img src=\"{$logo_path}logo.gif\" alt=\"\" /></div>\r\n            </td>\r\n        </tr>\r\n        <tr>\r\n            <td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\">---------------------------------------------------------------------------------------- <br />\r\n            Félicitations, vous avez un bon cadeau pour <b>{$AMMOUNT} </b>obtenu ! <br />\r\n            ----------------------------------------------------------------------------------------<br />\r\n            <br />\r\n            Ce bon vous a été envoyé par {$FROM_NAME},<br />\r\n            Avec le message:<br />\r\n            <br />\r\n            {$MESSAGE}<br />\r\n            <br />\r\n            Votre code de bon personnel est <strong>{$GIFT_CODE}</strong>. Vous pouvez soit réserver ce crédit pendant le processus de commande.<br />\r\n            <br />\r\n            Pour utiliser votre bon d\'échange, veuillez cliquer sur le lien suivant <a href=\"{$GIFT_LINK}\">{$GIFT_LINK}</a> <br />\r\n            <br />\r\n            S\'il y a des problèmes avec le lien ci-dessus lors de l\'échange, <br />\r\n            vous pouvez réserver le montant pendant le processus de commande. </font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>','0','mail','----------------------------------------------------------------------------------------\r\n   Félicitations, vous avez un bon cadeau pour {$AMMOUNT} obtenu !\r\n----------------------------------------------------------------------------------------\r\n\r\nCe bon vous a été envoyé par {$FROM_NAME},\r\nAvec le message:\r\n\r\n{$MESSAGE}\r\n\r\nVotre code de bon personnel est {$GIFT_CODE}. \r\n\r\nPour utiliser votre bon d\'échange, veuillez cliquer sur le lien suivant {$GIFT_LINK}\r\n\r\nS\'il y a des problèmes avec le lien ci-dessus lors de l\'échange,\r\nvous pouvez réserver le montant pendant le processus de commande.','Vous avez reçu un bon d\'achat d\'un ami.'),
('sepa_info',3,'<p>Le montant de la facture de {$PAYMENT_BANKTRANSFER_TOTAL} nous prélevons en tant que prélèvement SEPA à l\'échéance {$PAYMENT_BANKTRANSFER_DUE_DATE}<br />\r\nà la référence de votre mandat {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}<br />\r\net notre numéro d\'identification du créancier {$PAYMENT_BANKTRANSFER_CREDITOR_ID}<br />\r\nde votre compte {$PAYMENT_BANKTRANSFER_IBAN}<br />\r\nà la {$PAYMENT_BANKTRANSFER_BANKNAME} ein.</p>\r\n<p>Veuillez vous assurer qu\'il y a assez d\'argent disponible pour le paiement sur votre compte..</p>','0','mail','Le montant de la facture de {$PAYMENT_BANKTRANSFER_TOTAL} Nous prélevons l\'euro en tant que prélèvement SEPA à l\'échéance. {$PAYMENT_BANKTRANSFER_DUE_DATE}\r\nà la référence de votre mandat {$PAYMENT_BANKTRANSFER_MANDATE_REFERENCE}\r\net notre numéro d\'identification de créancier {$PAYMENT_BANKTRANSFER_CREDITOR_ID}\r\nde votre compte {$PAYMENT_BANKTRANSFER_IBAN}\r\nà la {$PAYMENT_BANKTRANSFER_BANKNAME} ein.\r\n\r\nVeuillez vous assurer qu\'il y a suffisamment d\'argent disponible sur votre compte pour le paiement.',''),
('sepa_mail',3,'<p>{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"}  {$PAYMENT_INFO_HTML}<br />\r\n[SIGNATUR]</p>','0','mail','{config_load file=\"$language/lang_$language.conf\" section=\"duty_info\"} \r\n\r\n{$PAYMENT_INFO_TXT}\r\n\r\n[SIGNATUR]','Votre commande par prélèvement SEPA.'),
('signatur',3,'<div><font size=\"1\" face=\"Verdana, Arial, Helvetica, sans-serif\">__________________________________________________________________<br />\r\n<br />\r\ninébranlable<br />\r\nAdresse<br />\r\nlocalisation<br />\r\npage d\'accueil<br />\r\nE-Mail<br />\r\ntéléphoner:<br />\r\nFax:<br />\r\nNuméro d\'identification TVA:<br />\r\nregistre du commerce<br />\r\nDirecteur Général:<br />\r\n</font></div>','0','mail','inébranlable\r\nAdresse\r\nlocalisation\r\npage d\'accueil\r\ncourriel\r\nFon :\r\nFax :\r\nUSt-IdNr.. :\r\nregistre du commerce\r\nDirecteur général :',''),
('stock_reorder_mail',3,'<p>Après la dernière vente du produit  {$PRODUCTS_NAME} l\'inventaire est fixé à {$PRODUCTS_CURRENT_QTY}  affaissé. Veuillez commander selon.</p>','0','mail','Après la dernière vente du produit  {$PRODUCTS_NAME} l\'inventaire est fixé à {$PRODUCTS_CURRENT_QTY}  a coulé. Veuillez commander à nouveau.','Message du magasin : Veuillez commander à nouveau.'),
('reminder_mail',3,'<table  width=\"100%\" border=\"0\" align=\"center\" cellpadding=\"4\" cellspacing=\"0\"><tr><td style=\"border-bottom: 1px solid; border-color: #cccccc;\"><div align=\"right\"><img src=\"{$logo_path}logo.gif\"></div></td></tr><tr><td><font size=\"2\" face=\"Verdana, Arial, Helvetica, sans-serif\"><strong>Cher client, </strong><br><br>dans la pièce jointe de cet e-mail nous vous enverrons le rappel de votre commande de {$ORDER_DATE}.<br /><br />Si vous avez des questions concernant votre commande, veuillez répondre à cet e-mail. Vous pouvez vérifier l\'état de votre commande<br />Vous pouvez voir sous: <a href=\"{$ORDER_LINK}\">{$ORDER_LINK}</a>.<br /></font></td></tr></table>','0','mail','Cher client, Dans la pièce jointe de cet e-mail nous vous envoyons le rappel de votre commande de {$ORDER_DATE}Si vous avez des questions concernant votre commande, veuillez répondre à cet e-mail, vous pouvez vérifier le statut de votre commande : {$ORDER_LINK}.','Rappel de votre commande auprès de {$ORDER_DATE}');
